import React, { useState } from 'react';
import Button from 'react-bootstrap/Button'; // Assuming you're using react-bootstrap for buttons

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  const validateEmail = (email) => {
    const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return re.test(String(email).toLowerCase());
  }

  const validateForm = () => {
    if (!email || !password) {
      setErrorMessage("Both fields are required.");
      return false;
    }
    if (!validateEmail(email)) {
      setErrorMessage("Invalid email format.");
      return false;
    }
    return true;
  }

  async function signUp() {
    if (!validateForm()) return;

    let data = { email, password };

    try {
      const response = await fetch("http://localhost:8000/api/verifyLogin-user", {
        method: 'POST',
        body: JSON.stringify(data),
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        }
      });

      const result = await response.json();
      console.log(result)
      if (response.ok) {
        setSuccessMessage("Login successful!");
        setErrorMessage("");
      } else {
        setErrorMessage(result.message || "Login failed.");
        setSuccessMessage("");
      }
    } catch (error) {
      setErrorMessage("An error occurred. Please try again.");
      setSuccessMessage("");
    }
  }

  return (
    <div>
      <div className="container">
        <div className="row justify-content-center">
          <div className="col-12 col-sm-10 col-md-8 col-lg-6 col-xl-5 col-xxl-4">
            <div className="card border border-light-subtle rounded-3 shadow-sm">
              <div className="card-body p-3 p-md-4 p-xl-5">
                <div className="text-center mb-3">
                </div>
                <h2 className="fs-6 fw-normal text-center text-secondary mb-4">Enter your details to Login</h2>
                <form onSubmit={(e) => { e.preventDefault(); signUp(); }}>
                  <div className="row gy-2 overflow-hidden">
                    <div className="col-12">
                      <div className="form-floating mb-3">
                        <input
                          type="email"
                          className="form-control"
                          name="email"
                          id="email"
                          value={email}
                          onChange={(e) => setEmail(e.target.value)}
                          placeholder="name@example.com"
                          required
                        />
                        <label htmlFor="email" className="form-label">Email</label>
                      </div>
                    </div>
                    <div className="col-12">
                      <div className="form-floating mb-3">
                        <input
                          type="password"
                          className="form-control"
                          name="password"
                          id="password"
                          value={password}
                          onChange={(e) => setPassword(e.target.value)}
                          placeholder="Password"
                          required
                        />
                        <label htmlFor="password" className="form-label">Password</label>
                      </div>
                    </div>
                    {errorMessage && <div className="col-12 text-danger">{errorMessage}</div>}
                    {successMessage && <div className="col-12 text-success">{successMessage}</div>}
                    <Button type="submit" className="btn btn-primary">Submit</Button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
